mapper.select:
select chatroom_id, chatuser1_id, chatuser2_id, requested_by, chatroom_status, updated_at, created_at
from ttm.chatrooms
where chatuser1_id = #{chatuser1Id}
or chatuser2_id = #{chatuser2Id}

select chatroom_id, chatuser1_id, chatuser2_id, requested_by, chatroom_status, updated_at, created_at
from ttm.chatrooms
where chatroom_id = #{chatroomId}

mapper.update:
update ttm.chatrooms
set chatuser1_id = #{chatuser1_id},
    chatuser2_id = #{chatuser2_id},
    chatroom_status = #{chatroomStatus},
    updated_at = #{updatedAt}
where chatuser1_id = #{chatuser1Id}
or chatuser2_id = #{chatuser2Id}
