mapper.update:
update pets
set pet_like_count = pet_like_count +1
where pet_id = #{value}


update pets
set pet_like_count = pet_like_count -1
where pet_like_count > 0 and pet_id = #{petId}

mapper.select:
select pet_id, pet_weight, pet_breed, pet_BirthDay, pet_Name, pet_Desc, pet_Gender, pet_like_count, user_id as petUserId, created_at
from pets
where pet_Id = #{value}

select pet_id, pet_weight, pet_breed, pet_BirthDay, pet_Name, pet_Desc, pet_Gender, pet_Like_Count, user_id as petUserId, created_at
from pets
where user_id = #{petUserId}

select COUNT(*)
from pets
where user_id = #{petUserId}
